import React, {useState} from 'react';
import Radium from 'radium';
import './App.css';
import Person from './Person/Person';

const App = props => {

  const [ personsState, setPersonsState ] = useState({
    persons: [
      { id:'1', name: 'Max', age: 28 }, 
      { id:'2', name: 'Jose', age: 23 }, 
      { id:'3', name: 'Thomas', age: 18 }
    ],
    showPersons: false,
  });

  const switchNameHandler = (nombre) => {
    //console.log('Was clicked!');
    //DONT DO THIS: this.state.persons[0].name = "Maximilia";
    setPersonsState({
      persons: [
        { name: nombre, age: 28 }, 
        { name: 'JOSE', age: 23 }, 
        { name: 'THOMAS', age: 18 }
      ],
      showPersons: true,
    });
  }

  const deletePersonHandler = (i) => {
    const persons = [...personsState.persons];
    persons.splice(i, 1);
    setPersonsState({persons: persons, showPersons:true});
  }

  const switchChangedHandler = (event, id) => {

    const personIndex = personsState.persons.findIndex(p => {
      return p.id === id;
    });

    const person = {
      ...personsState.persons[personIndex]
    };

    person.name = event.target.value;

    const persons = [...personsState.persons];
    persons[personIndex] = person;

    setPersonsState({persons: persons, showPersons:true});

  }

   const togglePersonsHandler = () => {
    const doesShow = personsState.showPersons;

    setPersonsState({
      persons: [
        { name: 'Max', age: 28 }, 
        { name: 'Jose', age: 23 }, 
        { name: 'Thomas', age: 18 }
      ],
    showPersons: !doesShow});
  };

  const style = {
    backgroundColor: 'green',
    color: 'white',
    font: 'inherit',
    border: '1px solid blue',
    padding: '8px',
    cursor: 'pointer',
    ':hover': {
      backgroundColor: 'lightgreen',
      color: 'black',
    }
  };

  let persons = null;

  if (personsState.showPersons) {
    persons = (
      <div>
        {
          personsState.persons.map( (person, index) => {
            return <Person
              click={() => deletePersonHandler(index)} 
              name={person.name} 
              changed={(event) => switchChangedHandler(event, person.id)} 
              age={person.age} 
              key={person.id}/>
            })            
          }
        </div> 
    );
    style.backgroundColor = 'red';
    style[':hover'] = {
      backgroundColor: 'salmon',
      color: 'black',
    };
  }

  let classes = [];

  if (personsState.persons.length <= 2) {
    classes.push('red'); //classes = ['red']
  }
  if (personsState.persons.length <= 1) {
    classes.push('bold'); //classes = ['red', 'bold']
  }

  return (
    <div className="App">
      <h1>Hola</h1>
      <p className={classes.join(' ')}>Esto funciona!!</p>
      <button onClick={togglePersonsHandler} 
      style={style}
      >
        Toggle Persons
      </button>
      { persons }
    </div>
  );
    /*var h1 = React.createElement('h1', null, 'Hola soy React!!')
    return React.createElement('div', {className: 'App'}, h1);*/

  };

export default Radium(App);
